import React, { useState } from 'react'
import axios from 'axios'

const Homepage = () => {
    const [name ,setName] = useState('')
    const [message,setMessage]= useState('')
    const data = {name:name,message:message}
    const sendMessage = () =>{
       axios.post('http://localhost:5000/mes')
    }
  return (
    <div className=''>
        <div className='h-20 '></div>
        <div className='flex justify-center mb-5'>

        <img className='h-40 border border-black rounded-full' src="https://www.aquasafemine.com/wp-content/uploads/2018/06/dummy-man-570x570.png" alt="" />
        </div>
        <span className='font-slim text-xl'>Gayathri Krishna</span>

       <div className="flex justify-center mt-8 ">  
        <input className='w-80 rounded-md py-1 bg-slate-200 shadow shadow-slate-500 shadow-md px-2' placeholder='Name' type="text" 
        onChange={(e)=>setName(e.target.value)}
        />
       </div>
        <textarea className='mt-5 w-80 rounded-md py-2 bg-slate-200 shadow shadow-slate-500 shadow-md px-2' name="" id="" cols="30" placeholder='Write your Question here!' rows="10"
        onChange={(e)=>setMessage(e.target.value)}
        ></textarea>
        <div className='w-screen  flex justify-center '>
        <div className="w-80 flex justify-end ">
        <button onClick={sendMessage} className='rounded-md bg-blue-400 hover:bg-blue-600 text-white px-3 py-1' type='submit'>Proceed</button>

        </div>
        </div>
    </div>
  )
}

export default Homepage
